import styled from 'styled-components'
import React from 'react'
import { ArrowLeftOutlined, ArrowRightOutlined } from '@material-ui/icons';

const Container = styled.div`
width: 100%;
height: 100vh;
display: flex;
position: relative;
`

const Arrow = styled.div`
width: 50px;
height: 50px;
background-color: #f7f8f7;
border-radius: 50%;
display: flex;
align-items: center;
justify-content: center;
position: absolute;
top: 0;
bottom: 0;
left: ${props=> props.direction === "left" && "10px"};
right: ${props=> props.direction === "right" && "10px"};
margin: auto;
cursor: pointer;
opacity: 0.75;
`

const Wrapper = styled.div`
height: 100%;
`
const Slide = styled.div`
width: 100vw;
height: 100vh; 
display: flex;
align-item: center;
`
const Imgcontainer = styled.div`
height: 100%;
flex:2;`

const Image = styled.img`
padding-top: 30px;
padding-left: 45px;
height: 75%;
`

const Image2 = styled.img`
padding-top: 30px;
padding-right:450px;
height: 75%;
`

const InfoContainer = styled.div`
flex:1;`

const Title = styled.h1``
const Desc = styled.p``
const Button = styled.button``


export const Slider = () => {
  return (
    <Container>
      <Arrow direction="left">
      <ArrowLeftOutlined/>
      </Arrow>
      <Wrapper>
        <Slide>
        <Imgcontainer>
        <Image src="https://images.unsplash.com/photo-1513135724701-30343e546328?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=458&q=80" />
        </Imgcontainer>
        <InfoContainer>
          <Title>Make Earth a better place to live!</Title>
          <Desc></Desc>
          <Button></Button>
        </InfoContainer>
        </Slide>
      </Wrapper>
      <Arrow direction="right">
      <ArrowRightOutlined/>
      </Arrow>
    </Container>
  )
}
