import styled from 'styled-components'
import React from 'react'
import { ArrowLeftOutlined, ArrowRightOutlined } from '@material-ui/icons';

const Container = styled.div`
width: 100vw;
height: 100vh;
display: flex;
position: relative;
overflow: hidden;
`

const Arrow = styled.div`
width: 50px;
height: 50px;
background-color: #f7f8f7;
border-radius: 50%;
display: flex;
align-items: center;
justify-content: center;
position: absolute;
top: 0;
bottom: 0;
left: ${props=> props.direction === "left" && "10px"};
right: ${props=> props.direction === "right" && "10px"};
margin: auto;
cursor: pointer;
opacity: 0.5;
z-index: 2;
`

const Wrapper = styled.div`
height: 100%;
display: flex;
transform: translateX(${props=>props.slideIndex * -100}vw);
`
const Slide = styled.div`
width: 100vw;
height: 100vh; 
display: flex;
align-item: center;
background-color: #${props=>props.bg}
`
const Imgcontainer = styled.div`
height: 100%;
flex:1;`

const Image1 = styled.img`
padding-top: 70px;
position: 'absolute'; 
top: 55; 
left: 20; 
zIndex: 1;
height: 56%;
`
const Image2 = styled.img`
padding-top: 45px;
padding-right: 45px;
height: 78%;
`

const InfoContainer = styled.div`
flex: auto;
padding: 50px;`

const Title = styled.h1`
font-size: 70px;`
const Desc = styled.p`
margin: 45px 0px;
font-size: 20px;
font-weight: 500px;
`
const Button = styled.button`
padding: 10px;
font-size: 20px;
color: #f7f8f7;
border-color: transparent;
background-color: #1A6837;
cursor: pointer;
`


export const Slider = () => {
    const [slideIndex, setSlideIndex] = React.useState(0);
    const handleClick = (direction) => {
      if(direction=="left"){
        setSlideIndex(slideIndex > 0 ? slideIndex -1 : 2);
      }else {
        setSlideIndex(slideIndex < 2 ? slideIndex +1 : 0);
      }
    };
  return (
    <Container>
      <Arrow direction="left" onClick={()=>handleClick("left")}>
      <ArrowLeftOutlined/>
      </Arrow>
      <Wrapper slideIndex={slideIndex}>
        <Slide bg="F7FFF7">
        <InfoContainer>
          <Title>Sustainability at your Doorstep!</Title>
          <Desc>We shield you from any toxic chemicals by substituting them with natural ingredients. Using environmentally friendly items increases your quality of life in terms of mortality, age, diseases, and illness.</Desc>
          <Button>Explore</Button>
        </InfoContainer>
        <Imgcontainer>
        <Image1 src="https://images.unsplash.com/photo-1595991209266-5ff5a3a2f008?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1887&q=80" />
        </Imgcontainer>
        <Imgcontainer><Image2 src="https://images.unsplash.com/photo-1513135724701-30343e546328?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=458&q=80" /></Imgcontainer>
        </Slide>
        <Slide bg="F7FFF7">
        <InfoContainer>
          <Title>Organic Clothes that matter!</Title>
          <Desc>Wear Clothes made from 100% Cotton without toxic substances. They are generally much better quality and best material for your skin.</Desc>
          <Button>Shop Now</Button>
        </InfoContainer>
        <Imgcontainer>
        <Image1 src="https://images.unsplash.com/photo-1595991209266-5ff5a3a2f008?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1887&q=80" />
        </Imgcontainer>
        <Imgcontainer><Image2 src="https://images.unsplash.com/photo-1513135724701-30343e546328?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=458&q=80" /></Imgcontainer>
        </Slide>
        <Slide bg="F7FFF7">
        <InfoContainer>
          <Title>Sustainability at your Doorstep!</Title>
          <Desc>We shield you from any toxic chemicals by substituting them with natural ingredients. Using environmentally friendly items increases your quality of life in terms of mortality, age, diseases, and illness.</Desc>
          <Button>Explore</Button>
        </InfoContainer>
        <Imgcontainer>
        <Image1 src="https://images.unsplash.com/photo-1595991209266-5ff5a3a2f008?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1887&q=80" />
        </Imgcontainer>
        <Imgcontainer><Image2 src="https://images.unsplash.com/photo-1513135724701-30343e546328?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=458&q=80" /></Imgcontainer>
        </Slide>
      </Wrapper>
      <Arrow direction="right" onClick={()=>handleClick("right")}>
      <ArrowRightOutlined/>
      </Arrow>
    </Container>
  )
}
