import { Facebook, Instagram, Pinterest } from '@material-ui/icons';
import styled from "styled-components"
import React from 'react'
import myLogo from './Logo.svg';

const Container =  styled.div`
display: flex;
`
const Left =  styled.div`
flex: 1;
display: flex;
flex-direction: column;
padding: 20px;
`
const Logo = styled.div``
const Description = styled.p`
margin: 20px 0px;
`
const SocialContainer = styled.div`
display: flex;`

const SocialIcon = styled.div`
width: 40px;
height: 40px;
border-radius: 50%;
color: white;
background-color: #${props=>props.color};
display: flex;
align-items: center;
justify-content: center;
margin-right: 20px;
`




const Center =  styled.div`
flex: 1;
`
const Right =  styled.div`
flex: 1;
`

const Footer = () => {
  return (
    <Container>
    <Left>
        <Logo><img src={myLogo} width={182} height={64}/></Logo>
        <Description>At Green Steam, we want you to put your trust in us as a marketplace and community. Striving to be a one-stop-shop for fashion forward, ethical, and sustainable goods, we vet all of our brands so you donâ€™t have to.</Description>
        <SocialContainer>
            <SocialIcon color="3B5999">
                <Facebook/>
            </SocialIcon>
            <SocialIcon color="E4405F">
                <Instagram/>
            </SocialIcon >
            <SocialIcon color="E60023">
                <Pinterest/>
            </SocialIcon>
        </SocialContainer>
    </Left>
    <Center></Center>
    <Right></Right>
    </Container>
  )
}

export default Footer
